        "$:/plugins/twpub/6a4248ca8f6e64ca/text/000000276": {
            "role": "text",
            "title": "$:/plugins/twpub/6a4248ca8f6e64ca/text/000000276",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-6a4248ca8f6e64ca-9c6670 twpub-6a4248ca8f6e64ca-e3b0c4 twpub-6a4248ca8f6e64ca-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/6a4248ca8f6e64ca/text/000000243\"><span class=\"label\">[18]</span></$link> <i>Sakaki</i>, the <i>Cleyera Japonica</i>, a sacred plant emblematic of purity, and much used at funerals and in the decoration of graves.</p></div></div></div>"
        },
        "$:/plugins/twpub/6a4248ca8f6e64ca/text/000000277": {
            "role": "text",
            "title": "$:/plugins/twpub/6a4248ca8f6e64ca/text/000000277",
