        "$:/plugins/twpub/6a4248ca8f6e64ca/text/000000812": {
            "role": "text",
            "title": "$:/plugins/twpub/6a4248ca8f6e64ca/text/000000812",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-6a4248ca8f6e64ca-9c6670 twpub-6a4248ca8f6e64ca-e3b0c4 twpub-6a4248ca8f6e64ca-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/6a4248ca8f6e64ca/text/000000792\"><span class=\"label\">[45]</span></$link> <i>Gohei</i>, a piece of white paper, cut and folded in a peculiar manner, one of the sacred symbols of the Shintō faith.</p></div></div></div>"
        },
        "$:/plugins/twpub/6a4248ca8f6e64ca/text/000000813": {
