        "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000011": {
            "role": "text",
            "title": "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000011",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-6d09b81e32b0d4bf-0fdc9a twpub-6d09b81e32b0d4bf-c3c16f twpub-6d09b81e32b0d4bf-6b4cd0\"><h3><i>WRITTEN BY HIMSELF.</i><br>\n<br>\n<br></h3></div>"
        },
        "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000012": {
            "role": "text",
            "title": "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000012",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-6d09b81e32b0d4bf-0fdc9a twpub-6d09b81e32b0d4bf-c3c16f twpub-6d09b81e32b0d4bf-6b4cd0\"><p class=\"center\"><i>Behold, God is my salvation; I will trust and not be<br>\nafraid, for the Lord Jehovah is my strength and my<br>\nsong; he also is become my salvation.<br>\nAnd in that shall ye say, Praise the Lord, call upon his<br>\nname, declare his doings among the people. Isaiah xii. 2, 4.</i><br>\n<br>\n<br>\n<br></p></div>"
        },
        "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000013": {
