        "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000124": {
            "role": "text",
            "title": "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000124",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-6d09b81e32b0d4bf-0fdc9a twpub-6d09b81e32b0d4bf-c3c16f twpub-6d09b81e32b0d4bf-6b4cd0\"><div class=\"footnotes\"><div class=\"footnote\"><p><a></a><$link class=\"pginternal\" to=\"$:/plugins/twpub/6d09b81e32b0d4bf/text/000000102\"><span class=\"label\">[A]</span></$link> See Benezet's &quot;Account of Guinea&quot; throughout.</p></div></div></div>"
        },
        "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000125": {
