        "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000420": {
            "role": "text",
            "title": "$:/plugins/twpub/6d09b81e32b0d4bf/text/000000420",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-6d09b81e32b0d4bf-0fdc9a twpub-6d09b81e32b0d4bf-c3c16f twpub-6d09b81e32b0d4bf-6b4cd0\"><div class=\"blockquot\"><p>'The bearer hereof, Gustavus Vassa, was my slave for upwards of three years, during which he has always behaved himself well, and discharged his duty with honesty and assiduity.</p></div></div>"
        },
