        "$:/plugins/twpub/6e05ecea40671c2d/text/000000007": {
            "role": "text",
            "title": "$:/plugins/twpub/6e05ecea40671c2d/text/000000007",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-6e05ecea40671c2d-b0e0ab twpub-6e05ecea40671c2d-92b109 twpub-6e05ecea40671c2d-6b4cd0\"><table class=\"c3\">\n<tbody><tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/6e05ecea40671c2d/text/000000009\">Chapter I. Into the Primitive</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/6e05ecea40671c2d/text/000000112\">Chapter II. The Law of Club and Fang</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/6e05ecea40671c2d/text/000000167\">Chapter III. The Dominant Primordial Beast</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/6e05ecea40671c2d/text/000000254\">Chapter IV. Who Has Won to Mastership</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/6e05ecea40671c2d/text/000000327\">Chapter V. The Toil of Trace</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/6e05ecea40671c2d/text/000000462\">Chapter VI. For the Love of a Man</$link></td>\n</tr>\n<tr>\n<td><$link class=\"pginternal\" to=\"$:/plugins/twpub/6e05ecea40671c2d/text/000000589\">Chapter VII. The Sounding of the Call</$link></td>\n</tr>\n</tbody></table></div>"
