        "$:/plugins/twpub/769d3732d4c38307/text/000000018": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000000018",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"center\">AL PROFESSORE<br>\nCAV. BIAGIO INGROIA,<br>\nPREZIOSO ALLEATO<br>\nL’AUTORE RICONOSCENTE.</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000000019": {
            "role": "text",
