        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002266": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002266",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000096\">[1]</$link><br>\n[ Black races are evidently known to the writer as stretching all across Africa, one half looking West on to the Atlantic, and the other East on to the Indian Ocean.]</p></div></div>"
        },
