        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002270": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002270",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000110\">[2]</$link><br>\n[ The original use of the footstool was probably less to rest the feet than to keep them (especially when bare) from a floor which was often wet and dirty.]</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002271": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002271",
