        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002272": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002272",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p><a></a></p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002273": {
