        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002282": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002282",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000118\">[5]</$link><br>\n[ i.e. “with a current in it”—see illustrations and map near the end of bks. v. and vi. respectively.]</p></div></div>"
        },
