        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002478": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002478",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000494\">[51]</$link><br>\n[ Irrigation as here described is common in gardens near Trapani. The water that supplies the ducts is drawn from wells by a mule who turns a wheel with buckets on it.]</p></div></div>"
        },
