        "$:/plugins/twpub/769d3732d4c38307/text/000002482": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002482",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000500\">[52]</$link><br>\n[ There is not a word here about the cattle of the sun-god.]</p></div></div>"
