        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002486": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002486",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000522\">[53]</$link><br>\n[ The writer evidently thought that green, growing wood might also be well seasoned.]</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002487": {
