        "$:/plugins/twpub/769d3732d4c38307/text/000002490": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002490",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000552\">[54]</$link><br>\n[ The reader will note that the river was flowing with salt water i.e. that it was tidal.]</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002491": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002491",
