        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002494": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002494",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000587\">[55]</$link><br>\n[ Then the Ogygian island was not so far off, but that Nausicaa might be assumed to know where it was.]</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002495": {
