        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002538": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002538",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000689\">[66]</$link><br>\n[ Translation very dubious. I suppose the {Greek} here to be the covered sheds that ran round the outer courtyard. See illustrations at the end of bk. iii.]</p></div></div>"
        },
