        "$:/plugins/twpub/769d3732d4c38307/text/000002578": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002578",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000000792\">[76]</$link><br>\n[ “Il.” xviii. 533-534. The sudden lapse into the third person here for a couple of lines is due to the fact that the two Iliadic lines taken are in the third person.]</p></div></div>"
        },
