        "$:/plugins/twpub/769d3732d4c38307/text/000002662": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002662",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000001076\">[97]</$link><br>\n[ Is the writer a man or a woman?]</p></div></div>"
