        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002772": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002772",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000001246\">[124]</$link><br>\n[ See map near the end of bk. vi. <i>Ruccazzù dei corvi</i> of course means “the rock of the ravens.” Both name and ravens still exist.]</p></div></div>"
        },
