        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002780": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002780",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000001261\">[126]</$link><br>\n[ The site I assign to Eumaeus’s hut, close to the <i>Ruccazzù dei corvi</i>, is about 2,000 feet above the sea, and commands an extensive view.]</p></div></div>"
        },
