        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002848": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002848",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000001562\">[141]</$link><br>\n[ The butchering and making ready the carcases took place partly in the outer yard and partly in the open part of the inner court.]</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002849": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002849",
