        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002992": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000002992",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000002081\">[176]</$link><br>\n[ But in xix. 500-502 Ulysses scolded Euryclea for offering information on this very point, and declared himself quite able to settle it for himself.]</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000002993": {
