        },
        "$:/plugins/twpub/769d3732d4c38307/text/000003030": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000003030",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000002162\">[184]</$link><br>\n[ Ulysses bedroom in the mind of the writer is here too apparently down stairs.]</p></div></div>"
        },
