            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\">\n</div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000003034": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000003034",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-769d3732d4c38307-4b6aff twpub-769d3732d4c38307-39c75b twpub-769d3732d4c38307-6b4cd0\"><div class=\"chapter\"><p class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/769d3732d4c38307/text/000002162\">[185]</$link><br>\n[ Penelope having been now sufficiently whitewashed, disappears from the poem.]</p></div></div>"
        },
        "$:/plugins/twpub/769d3732d4c38307/text/000003035": {
            "role": "text",
            "title": "$:/plugins/twpub/769d3732d4c38307/text/000003035",
