        },
        "$:/plugins/twpub/7746c4c1a4bc8be8/text/000004409": {
            "role": "text",
            "title": "$:/plugins/twpub/7746c4c1a4bc8be8/text/000004409",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-7746c4c1a4bc8be8-08e7ce twpub-7746c4c1a4bc8be8-42a937 twpub-7746c4c1a4bc8be8-6b4cd0\"><div class=\"c2\">Project Gutenberg™ is synonymous with the free distribution of electronic works in formats readable by the widest variety of computers including obsolete, old, middle-aged and new computers. It exists because of the efforts of hundreds of volunteers and donations from people in all walks of life.</div></div>"
        },
        "$:/plugins/twpub/7746c4c1a4bc8be8/text/000004410": {
