        },
        "$:/plugins/twpub/7746c4c1a4bc8be8/text/000004412": {
            "role": "text",
            "title": "$:/plugins/twpub/7746c4c1a4bc8be8/text/000004412",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-7746c4c1a4bc8be8-08e7ce twpub-7746c4c1a4bc8be8-42a937 twpub-7746c4c1a4bc8be8-6b4cd0\"><div class=\"c2\">The Project Gutenberg Literary Archive Foundation is a non-profit 501(c)(3) educational corporation organized under the laws of the state of Mississippi and granted tax exempt status by the Internal Revenue Service. The Foundation’s EIN or federal tax identification number is 64-6221541. Contributions to the Project Gutenberg Literary Archive Foundation are tax deductible to the full extent permitted by U.S. federal laws and your state’s laws.</div></div>"
        },
        "$:/plugins/twpub/7746c4c1a4bc8be8/text/000004413": {
