        },
        "$:/plugins/twpub/816c03388ec8918a/text/000000466": {
            "role": "text",
            "title": "$:/plugins/twpub/816c03388ec8918a/text/000000466",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-816c03388ec8918a-33146d twpub-816c03388ec8918a-40c3a2 twpub-816c03388ec8918a-6b4cd0\"><div class=\"c2\">The Project Gutenberg Literary Archive Foundation is a non-profit 501(c)(3) educational corporation organized under the laws of the state of Mississippi and granted tax exempt status by the Internal Revenue Service. The Foundation’s EIN or federal tax identification number is 64-6221541. Contributions to the Project Gutenberg Literary Archive Foundation are tax deductible to the full extent permitted by U.S. federal laws and your state’s laws.</div></div>"
        },
