            "title": "$:/plugins/twpub/816c03388ec8918a/text/000000472",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-816c03388ec8918a-33146d twpub-816c03388ec8918a-40c3a2 twpub-816c03388ec8918a-6b4cd0\"><div class=\"c2\">International donations are gratefully accepted, but we cannot make any statements concerning tax treatment of donations received from outside the United States. U.S. laws alone swamp our small staff.</div></div>"
        },
        "$:/plugins/twpub/816c03388ec8918a/text/000000473": {
