        },
        "$:/plugins/twpub/8cb0b9a387f4bca8/text/000003467": {
            "role": "text",
            "title": "$:/plugins/twpub/8cb0b9a387f4bca8/text/000003467",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-8cb0b9a387f4bca8-8170b9 twpub-8cb0b9a387f4bca8-e335ae twpub-8cb0b9a387f4bca8-6b4cd0\"><div class=\"c15\">\n</div></div>"
        },
        "$:/plugins/twpub/8cb0b9a387f4bca8/text/000003468": {
            "role": "text",
            "title": "$:/plugins/twpub/8cb0b9a387f4bca8/text/000003468",
