        "$:/plugins/twpub/978868d5b1324e00/text/000000272": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000272",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-978868d5b1324e00-9ce61e twpub-978868d5b1324e00-6f4644 twpub-978868d5b1324e00-6b4cd0\"><blockquote><div><p class=\"noindent\">“No writer has exceeded Paine in ease and familiarity of style, in perspicuity of expression, happiness of elucidation, and in simple and unassuming language.”</p></div></blockquote></div>"
        },
