        "$:/plugins/twpub/978868d5b1324e00/text/000000276": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000276",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-978868d5b1324e00-9ce61e twpub-978868d5b1324e00-6f4644 twpub-978868d5b1324e00-6b4cd0\"><blockquote><div>\n</div></blockquote></div>"
        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000277": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000277",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-978868d5b1324e00-9ce61e twpub-978868d5b1324e00-6f4644 twpub-978868d5b1324e00-6b4cd0\"><blockquote><div><p class=\"noindent\">“A pamphlet called ‘Commonsense’ makes a great noise. One of the vilest things that ever was published to the world. Full of false representations, lies, calumny, and treason, whose principles are to subvert all Kingly Governments and erect an Independent Republic.”</p></div></blockquote></div>"
        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000278": {
            "role": "text",
