        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000279": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000279",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-978868d5b1324e00-9ce61e twpub-978868d5b1324e00-6f4644 twpub-978868d5b1324e00-6b4cd0\"><blockquote><div><p class=\"right noindent\"><em>Nicholas Cresswell</em></p></div></blockquote></div>"
        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000280": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000280",
