        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000281": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000281",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-978868d5b1324e00-9ce61e twpub-978868d5b1324e00-6f4644 twpub-978868d5b1324e00-6b4cd0\"><blockquote><div>\n</div></blockquote></div>"
        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000282": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000282",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-978868d5b1324e00-9ce61e twpub-978868d5b1324e00-6f4644 twpub-978868d5b1324e00-6b4cd0\"><blockquote><div><p class=\"noindent\">“I dreaded the effect so popular a pamphlet might have among the people, and determined to do all in my Power to counteract the effect of it.”</p></div></blockquote></div>"
        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000283": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000283",
