        "$:/plugins/twpub/978868d5b1324e00/text/000000287": {
            "role": "text",
            "title": "$:/plugins/twpub/978868d5b1324e00/text/000000287",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-978868d5b1324e00-9ce61e twpub-978868d5b1324e00-6f4644 twpub-978868d5b1324e00-6b4cd0\"><blockquote><div><p class=\"noindent\">“Its effects were sudden and extensive upon the American mind. It was read by public men.”</p></div></blockquote></div>"
        },
        "$:/plugins/twpub/978868d5b1324e00/text/000000288": {
