        "$:/plugins/twpub/9f0a66bf42729524/text/000004474": {
            "role": "text",
            "title": "$:/plugins/twpub/9f0a66bf42729524/text/000004474",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-9f0a66bf42729524-9b89d6 twpub-9f0a66bf42729524-054be7 twpub-9f0a66bf42729524-6b4cd0\"><div class=\"c15\"><div class=\"c2\">Project Gutenberg™ depends upon and cannot survive without widespread public support and donations to carry out its mission of increasing the number of public domain and licensed works that can be freely distributed in machine-readable form accessible by the widest array of equipment including outdated equipment. Many small donations ($1 to $5,000) are particularly important to maintaining tax exempt status with the IRS.</div></div></div>"
        },
        "$:/plugins/twpub/9f0a66bf42729524/text/000004475": {
