        },
        "$:/plugins/twpub/9f0a66bf42729524/text/000004480": {
            "role": "text",
            "title": "$:/plugins/twpub/9f0a66bf42729524/text/000004480",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-9f0a66bf42729524-9b89d6 twpub-9f0a66bf42729524-054be7 twpub-9f0a66bf42729524-6b4cd0\"><div class=\"c15\"><div class=\"c2\">International donations are gratefully accepted, but we cannot make any statements concerning tax treatment of donations received from outside the United States. U.S. laws alone swamp our small staff.</div></div></div>"
        },
