        "$:/plugins/twpub/a9183b8f0c72400a/text/000001604": {
            "role": "text",
            "title": "$:/plugins/twpub/a9183b8f0c72400a/text/000001604",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-a9183b8f0c72400a-f7c9a8 twpub-a9183b8f0c72400a-05dbf3 twpub-a9183b8f0c72400a-6b4cd0\"><div class=\"chapter\"><p><$link class=\"pginternal\" to=\"$:/plugins/twpub/a9183b8f0c72400a/text/000000208\">XVI. To steal, to sow, to buy, to be at rest, to see what is to be done</$link></p></div></div>"
        },
        "$:/plugins/twpub/a9183b8f0c72400a/text/000001605": {
