        },
        "$:/plugins/twpub/a9183b8f0c72400a/text/000001612": {
            "role": "text",
            "title": "$:/plugins/twpub/a9183b8f0c72400a/text/000001612",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-a9183b8f0c72400a-f7c9a8 twpub-a9183b8f0c72400a-05dbf3 twpub-a9183b8f0c72400a-6b4cd0\"><div class=\"chapter\"><p><$link class=\"pginternal\" to=\"$:/plugins/twpub/a9183b8f0c72400a/text/000000215\">I. That inward mistress part of man if it be in its own true natural</$link></p></div></div>"
        },
        "$:/plugins/twpub/a9183b8f0c72400a/text/000001613": {
            "role": "text",
            "title": "$:/plugins/twpub/a9183b8f0c72400a/text/000001613",
