        "$:/plugins/twpub/b444daeafa3e6711/text/000000232": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000000232",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1\"><p>B. = Bugge. C. = Cosijn. Gr. = Grein. Grdvtg. = Grundtvig. H. = Heyne. H. and S. = Harrison and Sharp. H.-So. = Heyne-Socin. K.= Kemble. Kl. = Kluge. M.= Müllenhoff. R. = Rieger. S. = Sievers. Sw. = Sweet. t.B. = ten Brink. Th. = Thorpe. W. = Wülcker.</p></div></div>"
        },
