        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001210": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001210",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001211": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001211",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000055\">V.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001212": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001212",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001213": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001213",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>THE GEATS REACH HEOROT.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001214": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001214",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf courteously replies.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001215": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001215",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The chief of the strangers rendered him answer,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001216": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001216",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001217": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001217",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">War-troopers’ leader, and word-treasure opened:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001218": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001218",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">We are Geats.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001219": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001219",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“We are sprung from the lineage of the people of Geatland,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001220": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001220",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001221": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001221",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And Higelac’s hearth-friends. To heroes unnumbered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001222": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001222",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">My father Ecgtheow was well-known in his day.</span> <span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001223": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001223",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">My father was known, a noble head-warrior</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001224": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001224",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001225": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001225",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ecgtheow titled; many a winter</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001226": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001226",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001227": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001227",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He lived with the people, ere he passed on his journey,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001228": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001228",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001229": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001229",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Old from his dwelling; each of the counsellors</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001230": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001230",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001231": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001231",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Widely mid world-folk well remembers him.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001232": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001232",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Our intentions towards King Hrothgar are of the kindest.</span> <span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001233": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001233",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">We, kindly of spirit, the lord of thy people,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001234": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001234",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001235": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001235",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The son of King Healfdene, have come here to visit,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001236": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001236",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001237": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001237",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Folk-troop’s defender: be free in thy counsels!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001238": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001238",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001239": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001239",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the noble one bear we a weighty commission,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001240": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001240",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001241": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001241",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The helm of the Danemen; we shall hide, I ween,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001242": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001242",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Is it true that a monster is slaying Danish heroes?</span> <span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001243": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001243",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Naught of our message. Thou know’st if it happen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001244": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001244",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001245": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001245",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">As we soothly heard say, that some savage despoiler,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001246": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001246",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001247": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001247",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Some hidden pursuer, on nights that are murky</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001248": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001248",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001249": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001249",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By deeds very direful ’mid the Danemen exhibits</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001250": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001250",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001251": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001251",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hatred unheard of, horrid destruction</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001252": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001252",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001253": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001253",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the falling of dead. From feelings least selfish</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001254": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001254",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I can help your king to free himself from this horrible creature.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001255": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001255",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I am able to render counsel to Hrothgar,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001256": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001256",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001257": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001257",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">How he, wise and worthy, may worst the destroyer,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001258": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001258",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001259": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001259",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">If the anguish of sorrow should ever be lessened,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001338\">1</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001260": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001260",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001261": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001261",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Comfort come to him, and care-waves grow cooler,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001262": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001262",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001263": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001263",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Or ever hereafter he agony suffer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001264": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001264",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001265": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001265",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And troublous distress, while towereth upward</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001266": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001266",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001267": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001267",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The handsomest of houses high on the summit.”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001268": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001268",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The coast-guard reminds Beowulf that it is easier to say than to do.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001269": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001269",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bestriding his stallion, the strand-watchman answered,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001270": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001270",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001271": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001271",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The doughty retainer: “The difference surely</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001272": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001272",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001273": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001273",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Twixt words and works, the warlike shield-bearer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001274": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001274",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001275": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001275",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who judgeth wisely well shall determine.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001276": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001276",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001277": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001277",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">This band, I hear, beareth no malice</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001278": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001278",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I am satisfied of your good intentions, and shall lead you to the palace.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001279": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001279",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the prince of the Scyldings. Pass ye then onward</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001280": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001280",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001281": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001281",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With weapons and armor. I shall lead you in person;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001282": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001282",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001283": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001283",
            "type": "text/vnd.twpub",
