            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To keep from all injury your excellent vessel,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001286": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001286",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Your boat shall be well cared for during your stay here.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001287": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001287",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Your fresh-tarred craft, ’gainst every opposer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001288": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001288",
            "type": "text/vnd.twpub",
