            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001289",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Close by the sea-shore, till the curved-neckèd bark shall</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001290": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001290",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001291": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001291",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Waft back again the well-beloved hero</div></div></div></div>"
        },
