        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001293": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001293",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er the way of the water to Weder dominions.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001294": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001294",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He again compliments Beowulf.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001295": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001295",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To warrior so great ’twill be granted sure</div></div></div></div>"
        },
