        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001309": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001309",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The heroes hastened, hurried the liegemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001310": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001310",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The land is perhaps rolling.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001311": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001311",
