        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001316": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001316",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001317": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001317",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of halls under heaven which the hero abode in;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001318": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001318",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001319": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001319",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Its lustre enlightened lands without number.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001320": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001320",
