            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001321": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001321",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then the battle-brave hero showed them the glittering</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001322": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001322",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001323": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001323",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Court of the bold ones, that they easily thither</div></div></div></div>"
        },
