            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001648": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001648",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000061\">VIII.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001649": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001649",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001650": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001650",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>HROTHGAR AND BEOWULF.—<i>Continued</i>.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001651": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001651",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar responds.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001652": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001652",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hrothgar discoursed, helm of the Scyldings:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001653": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001653",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001654": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001654",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“To defend our folk and to furnish assistance,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001737\">1</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001655": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001655",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001656": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001656",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thou soughtest us hither, good friend Beowulf.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001657": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001657",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Reminiscences of Beowulf’s father, Ecgtheow.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001658": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001658",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The fiercest of feuds thy father engaged in,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001659": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001659",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001660": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001660",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Heatholaf killed he in hand-to-hand conflict</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001661": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001661",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001662": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001662",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Mid Wilfingish warriors; then the Wederish people</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001663": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001663",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001664": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001664",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For fear of a feud were forced to disown him.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001665": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001665",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001666": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001666",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thence flying he fled to the folk of the South-Danes,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001667": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001667",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001668": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001668",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The race of the Scyldings, o’er the roll of the waters;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001669": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001669",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001670": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001670",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I had lately begun then to govern the Danemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001671": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001671",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001672": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001672",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The hoard-seat of heroes held in my youth,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001673": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001673",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001674": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001674",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Rich in its jewels: dead was Heregar,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001675": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001675",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001676": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001676",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">My kinsman and elder had earth-joys forsaken,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001677": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001677",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001678": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001678",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Healfdene his bairn. He was better than I am!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001679": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001679",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001680": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001680",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That feud thereafter for a fee I compounded;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001681": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001681",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001682": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001682",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er the weltering waters to the Wilfings I sent</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001683": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001683",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001684": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001684",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ornaments old; oaths did he swear me.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001685": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001685",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar recounts to Beowulf the horrors of Grendel’s persecutions.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001686": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001686",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">It pains me in spirit to any to tell it,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001687": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001687",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001688": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001688",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">What grief in Heorot Grendel hath caused me,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001689": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001689",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001690": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001690",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">What horror unlooked-for, by hatred unceasing.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001691": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001691",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001692": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001692",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Waned is my war-band, wasted my hall-troop;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001693": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001693",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001694": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001694",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Weird hath offcast them to the clutches of Grendel.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001695": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001695",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001696": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001696",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">God can easily hinder the scather</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001697": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001697",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001698": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001698",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From deeds so direful. Oft drunken with beer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001699": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001699",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">My thanes have made many boasts, but have not executed them.</span> <span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001700": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001700",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er the ale-vessel promised warriors in armor</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001701": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001701",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001702": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001702",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">They would willingly wait on the wassailing-benches</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001703": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001703",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001704": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001704",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A grapple with Grendel, with grimmest of edges.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001705": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001705",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001706": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001706",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then this mead-hall at morning with murder was reeking,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001707": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001707",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001708": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001708",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The building was bloody at breaking of daylight,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001709": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001709",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001710": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001710",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The bench-deals all flooded, dripping and bloodied,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001711": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001711",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001712": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001712",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The folk-hall was gory: I had fewer retainers,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001713": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001713",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001714": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001714",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Dear-beloved warriors, whom death had laid hold of.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001715": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001715",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Sit down to the feast, and give us comfort.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001716": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001716",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sit at the feast now, thy intents unto heroes,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001747\">2</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001717": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001717",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001718": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001718",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thy victor-fame show, as thy spirit doth urge thee!”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001719": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001719",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">A bench is made ready for Beowulf and his party.</span> <span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001720": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001720",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For the men of the Geats then together assembled,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001721": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001721",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001722": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001722",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In the beer-hall blithesome a bench was made ready;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001723": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001723",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001724": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001724",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">There warlike in spirit they went to be seated,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001725": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001725",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001726": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001726",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Proud and exultant. A liegeman did service,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001727": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001727",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001728": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001728",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who a beaker embellished bore with decorum,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001729": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001729",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The gleeman sings</span> <span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001730": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001730",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And gleaming-drink poured. The gleeman sang whilom</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001731": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001731",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The heroes all rejoice together.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001732": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001732",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hearty in Heorot; there was heroes’ rejoicing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001733": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001733",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001734": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001734",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A numerous war-band of Weders and Danemen.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001735": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001735",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001736": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001736",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001737": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001737",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001654\">[1]</$link> B. and S. reject the reading given in H.-So., and suggested by Grtvg. B. suggests for 457-458:\n</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001738": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001738",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><blockquote class=\"ang\"><div>\n</div></blockquote></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001739": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001739",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><blockquote class=\"ang\"><div><div class=\"l\">wáere-ryhtum Þú, wine mín Béowulf,</div></div></blockquote></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001740": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001740",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><blockquote class=\"ang\"><div>\n</div></blockquote></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001741": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001741",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><blockquote class=\"ang\"><div><div class=\"l\">and for ár-stafum úsic sóhtest.</div></div></blockquote></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001742": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001742",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><blockquote class=\"ang\"><div>\n</div></blockquote></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001743": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001743",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\">\n</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001744": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001744",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><p>This means: <i>From the obligations of clientage, my friend Beowulf, and for assistance thou hast sought us</i>.—This gives coherence to Hrothgar’s opening remarks in VIII., and also introduces a new motive for Beowulf’s coming to Hrothgar’s aid.</p></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001745": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001745",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\">\n</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001746": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001746",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001747": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001747",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001716\">[2]</$link> <i>Sit now at the feast, and disclose thy purposes to the victorious heroes, as thy spirit urges</i>.—Kl. reaches the above translation by erasing the comma after ‘<span class=\"ang\">meoto</span>’ and reading ‘<span class=\"ang\">sige-hrèðsecgum</span>.’—There are other and bolder emendations and suggestions. Of these the boldest is to regard ‘<span class=\"ang\">meoto</span>’ as a verb (imperative), and read ‘<span class=\"ang\">on sæl</span>’: <i>Think upon gayety, etc</i>.—All the renderings are unsatisfactory, the one given in our translation involving a zeugma.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001748": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001748",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001749": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001749",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001750": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001750",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001751": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001751",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001752": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001752",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000063\">IX.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001753": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001753",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001754": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001754",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>UNFERTH TAUNTS BEOWULF.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001755": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001755",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Unferth, a thane of Hrothgar, is jealous of Beowulf, and undertakes to twit him.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001756": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001756",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Unferth spoke up, Ecglaf his son,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001757": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001757",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001758": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001758",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who sat at the feet of the lord of the Scyldings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001759": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001759",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001760": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001760",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Opened the jousting (the journey<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001877\">1</$link></sup> of Beowulf,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001761": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001761",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001762": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001762",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sea-farer doughty, gave sorrow to Unferth</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001763": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001763",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001764": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001764",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And greatest chagrin, too, for granted he never</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001765": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001765",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001766": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001766",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That any man else on earth should attain to,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001767": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001767",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001768": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001768",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gain under heaven, more glory than he):</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001769": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001769",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Did you take part in a swimming-match with Breca?</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001770": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001770",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“Art thou that Beowulf with Breca did struggle,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001771": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001771",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001772": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001772",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On the wide sea-currents at swimming contended,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001773": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001773",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001774": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001774",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Where to humor your pride the ocean ye tried,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001775": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001775",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">’Twas mere folly that actuated you both to risk your lives on the ocean.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001776": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001776",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From vainest vaunting adventured your bodies</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001777": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001777",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001778": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001778",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In care of the waters? And no one was able</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001779": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001779",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001780": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001780",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nor lief nor loth one, in the least to dissuade you</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001781": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001781",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001782": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001782",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Your difficult voyage; then ye ventured a-swimming,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001783": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001783",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001784": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001784",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Where your arms outstretching the streams ye did cover,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001785": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001785",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001786": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001786",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The mere-ways measured, mixing and stirring them,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001787": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001787",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001788": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001788",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Glided the ocean; angry the waves were,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001789": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001789",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001790": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001790",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With the weltering of winter. In the water’s possession,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001791": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001791",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001792": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001792",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ye toiled for a seven-night; he at swimming outdid thee,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001793": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001793",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001794": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001794",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In strength excelled thee. Then early at morning</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001795": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001795",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001796": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001796",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On the Heathoremes’ shore the holm-currents tossed him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001797": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001797",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001798": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001798",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sought he thenceward the home of his fathers,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001799": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001799",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001800": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001800",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beloved of his liegemen, the land of the Brondings,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001801": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001801",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001802": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001802",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The peace-castle pleasant, where a people he wielded,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001803": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001803",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001804": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001804",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Had borough and jewels. The pledge that he made thee</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001805": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001805",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Breca outdid you entirely.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001806": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001806",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The son of Beanstan hath soothly accomplished.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001807": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001807",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001808": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001808",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then I ween thou wilt find thee less fortunate issue,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001809": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001809",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Much more will Grendel outdo you, if you vie with him in prowess.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001810": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001810",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Though ever triumphant in onset of battle,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001811": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001811",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001812": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001812",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A grim grappling, if Grendel thou darest</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001813": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001813",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001814": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001814",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For the space of a night near-by to wait for!”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001815": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001815",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf retaliates.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001816": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001816",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf answered, offspring of Ecgtheow:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001817": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001817",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001818": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001818",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“My good friend Unferth, sure freely and wildly,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001819": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001819",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">O friend Unferth, you are fuddled with beer, and cannot talk coherently.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001820": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001820",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thou fuddled with beer of Breca hast spoken,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001821": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001821",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001822": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001822",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hast told of his journey! A fact I allege it,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001823": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001823",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001824": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001824",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That greater strength in the waters I had then,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001825": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001825",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001826": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001826",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ills in the ocean, than any man else had.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001827": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001827",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001828": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001828",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">We made agreement as the merest of striplings</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001829": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001829",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001830": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001830",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Promised each other (both of us then were</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001831": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001831",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">We simply kept an engagement made in early life.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001832": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001832",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Younkers in years) that we yet would adventure</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001833": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001833",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001834": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001834",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Out on the ocean; it all we accomplished.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001835": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001835",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001836": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001836",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">While swimming the sea-floods, sword-blade unscabbarded</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001837": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001837",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001838": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001838",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Boldly we brandished, our bodies expected</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001839": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001839",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001840": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001840",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To shield from the sharks. He sure was unable</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001841": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001841",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He <i>could</i> not excel me, and I <i>would</i> not excel him.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001842": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001842",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To swim on the waters further than I could,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001843": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001843",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001844": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001844",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">More swift on the waves, nor <i>would</i> I from him go.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001845": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001845",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001846": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001846",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then we two companions stayed in the ocean</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001847": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001847",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">After five days the currents separated us.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001848": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001848",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Five nights together, till the currents did part us,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001849": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001849",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001850": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001850",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The weltering waters, weathers the bleakest,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001851": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001851",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001852": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001852",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And nethermost night, and the north-wind whistled</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001853": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001853",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001854": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001854",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fierce in our faces; fell were the billows.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001855": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001855",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001856": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001856",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The mere fishes’ mood was mightily ruffled:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001857": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001857",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001858": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001858",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And there against foemen my firm-knotted corslet,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001859": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001859",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001860": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001860",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hand-jointed, hardy, help did afford me;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001861": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001861",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001862": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001862",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">My battle-sark braided, brilliantly gilded,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001863": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001863",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">A horrible sea-beast attacked me, but I slew him.</span> <span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001864": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001864",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Lay on my bosom. To the bottom then dragged me,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001865": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001865",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001866": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001866",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A hateful fiend-scather, seized me and held me,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001867": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001867",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001868": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001868",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Grim in his grapple: ’twas granted me, nathless,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001869": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001869",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001870": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001870",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To pierce the monster with the point of my weapon,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001871": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001871",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001872": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001872",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">My obedient blade; battle offcarried</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001873": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001873",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001874": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001874",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The mighty mere-creature by means of my hand-blow.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001875": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001875",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001876": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001876",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001877": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001877",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001760\">[1]</$link> It has been plausibly suggested that ‘<span class=\"ang\">síð</span>’ (in 501 and in 353) means ‘arrival.’ If so, translate the bracket: <i>(the arrival of Beowulf, the brave seafarer, was a source of great chagrin to Unferth, etc.)</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001878": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001878",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001879": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001879",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001880": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001880",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001881": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001881",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001882": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001882",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000065\">X.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001883": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001883",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001884": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001884",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>BEOWULF SILENCES UNFERTH.—GLEE IS HIGH.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001885": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001885",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001886": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001886",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“So ill-meaning enemies often did cause me</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001887": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001887",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001888": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001888",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sorrow the sorest. I served them, in quittance,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001889": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001889",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">My dear sword always served me faithfully.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001890": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001890",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With my dear-lovèd sword, as in sooth it was fitting;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001891": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001891",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001892": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001892",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">They missed the pleasure of feasting abundantly,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001893": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001893",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001894": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001894",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ill-doers evil, of eating my body,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001895": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001895",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001896": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001896",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of surrounding the banquet deep in the ocean;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001897": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001897",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001898": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001898",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But wounded with edges early at morning</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001899": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001899",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001900": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001900",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">They were stretched a-high on the strand of the ocean,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001901": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001901",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I put a stop to the outrages of the sea-monsters.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001902": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001902",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Put to sleep with the sword, that sea-going travelers</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001903": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001903",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001904": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001904",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">No longer thereafter were hindered from sailing</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001905": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001905",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001906": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001906",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The foam-dashing currents. Came a light from the east,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001907": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001907",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001908": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001908",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">God’s beautiful beacon; the billows subsided,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001909": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001909",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001910": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001910",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That well I could see the nesses projecting,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001911": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001911",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Fortune helps the brave earl.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001912": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001912",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The blustering crags. Weird often saveth</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001913": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001913",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001914": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001914",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The undoomed hero if doughty his valor!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001915": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001915",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001916": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001916",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But me did it fortune<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002093\">1</$link></sup> to fell with my weapon</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001917": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001917",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001918": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001918",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nine of the nickers. Of night-struggle harder</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001919": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001919",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001920": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001920",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Neath dome of the heaven heard I but rarely,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001921": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001921",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001922": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001922",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nor of wight more woful in the waves of the ocean;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001923": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001923",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001924": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001924",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Yet I ’scaped with my life the grip of the monsters,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001925": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001925",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">After that escape I drifted to Finland.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001926": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001926",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Weary from travel. Then the waters bare me</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001927": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001927",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001928": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001928",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the land of the Finns, the flood with the current,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001929": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001929",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I have never heard of your doing any such bold deeds.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001930": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001930",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The weltering waves. Not a word hath been told me</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001931": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001931",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001932": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001932",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of deeds so daring done by thee, Unferth,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001933": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001933",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001934": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001934",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And of sword-terror none; never hath Breca</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001935": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001935",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001936": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001936",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">At the play of the battle, nor either of you two,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001937": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001937",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001938": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001938",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Feat so fearless performèd with weapons</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001939": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001939",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001940": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001940",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Glinting and gleaming . . . . . . . . . . . .</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001941": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001941",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001942": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001942",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">. . . . . . . . . . . . I utter no boasting;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001943": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001943",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">You are a slayer of brothers, and will suffer damnation, wise as you may be.</span> <span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001944": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001944",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Though with cold-blooded cruelty thou killedst thy brothers,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001945": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001945",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001946": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001946",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thy nearest of kin; thou needs must in hell get</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001947": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001947",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001948": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001948",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Direful damnation, though doughty thy wisdom.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001949": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001949",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001950": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001950",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I tell thee in earnest, offspring of Ecglaf,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001951": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001951",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001952": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001952",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Never had Grendel such numberless horrors,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001953": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001953",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001954": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001954",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The direful demon, done to thy liegelord,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001955": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001955",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001956": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001956",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Harrying in Heorot, if thy heart were as sturdy,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001957": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001957",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Had your acts been as brave as your words, Grendel had not ravaged your land so long.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001958": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001958",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thy mood as ferocious as thou dost describe them.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001959": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001959",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001960": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001960",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He hath found out fully that the fierce-burning hatred,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001961": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001961",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001962": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001962",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The edge-battle eager, of all of your kindred,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001963": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001963",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001964": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001964",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of the Victory-Scyldings, need little dismay him:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001965": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001965",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001966": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001966",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Oaths he exacteth, not any he spares</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001967": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001967",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The monster is not afraid of the Danes,</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001968": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001968",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of the folk of the Danemen, but fighteth with pleasure,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001969": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001969",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001970": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001970",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Killeth and feasteth, no contest expecteth</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001971": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001971",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">but he will soon learn to dread the Geats.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001972": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001972",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From Spear-Danish people. But the prowess and valor</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001973": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001973",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001974": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001974",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of the earls of the Geatmen early shall venture</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001975": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001975",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001976": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001976",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To give him a grapple. He shall go who is able</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001977": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001977",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001978": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001978",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bravely to banquet, when the bright-light of morning</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001979": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001979",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">On the second day, any warrior may go unmolested to the mead-banquet.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001980": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001980",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Which the second day bringeth, the sun in its ether-robes,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001981": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001981",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001982": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001982",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er children of men shines from the southward!”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001983": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001983",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001984": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001984",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then the gray-haired, war-famed giver of treasure</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001985": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001985",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar’s spirits are revived.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001986": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001986",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was blithesome and joyous, the Bright-Danish ruler</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001987": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001987",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001988": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001988",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Expected assistance; the people’s protector</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001989": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001989",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The old king trusts Beowulf. The heroes are joyful.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001990": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001990",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Heard from Beowulf his bold resolution.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001991": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001991",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001992": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001992",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">There was laughter of heroes; loud was the clatter,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001993": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001993",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001994": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001994",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The words were winsome. Wealhtheow advanced then,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001995": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001995",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Queen Wealhtheow plays the hostess.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001996": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001996",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Consort of Hrothgar, of courtesy mindful,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001997": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001997",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001998": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001998",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gold-decked saluted the men in the building,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000001999": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000001999",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002000": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002000",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the freeborn woman the beaker presented</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002001": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002001",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">She offers the cup to her husband first.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002002": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002002",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the lord of the kingdom, first of the East-Danes,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002003": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002003",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002004": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002004",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bade him be blithesome when beer was a-flowing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002005": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002005",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002006": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002006",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Lief to his liegemen; he lustily tasted</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002007": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002007",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002008": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002008",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of banquet and beaker, battle-famed ruler.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002009": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002009",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002010": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002010",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The Helmingish lady then graciously circled</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002011": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002011",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002012": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002012",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Mid all the liegemen lesser and greater:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002013": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002013",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a> <span class=\"sidenote\">She gives presents to the heroes.</span> <span class=\"linenum\">65</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002014": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002014",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Treasure-cups tendered, till time was afforded</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002015": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002015",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002016": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002016",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That the decorous-mooded, diademed folk-queen</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002017": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002017",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Then she offers the cup to Beowulf, thanking God that aid has come.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002018": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002018",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Might bear to Beowulf the bumper o’errunning;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002019": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002019",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002020": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002020",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">She greeted the Geat-prince, God she did thank,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002021": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002021",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002022": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002022",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Most wise in her words, that her wish was accomplished,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002023": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002023",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">70</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002024": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002024",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That in any of earlmen she ever should look for</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002025": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002025",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002026": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002026",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Solace in sorrow. He accepted the beaker,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002027": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002027",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002028": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002028",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Battle-bold warrior, at Wealhtheow’s giving,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002029": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002029",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf states to the queen the object of his visit.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002030": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002030",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then equipped for combat quoth he in measures,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002031": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002031",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002032": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002032",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf spake, offspring of Ecgtheow:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002033": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002033",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">75</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002034": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002034",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“I purposed in spirit when I mounted the ocean,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002035": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002035",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I determined to do or die.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002036": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002036",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When I boarded my boat with a band of my liegemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002037": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002037",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002038": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002038",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">I would work to the fullest the will of your people</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002039": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002039",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002040": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002040",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Or in foe’s-clutches fastened fall in the battle.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002041": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002041",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002042": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002042",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Deeds I shall do of daring and prowess,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002043": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002043",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">80</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002044": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002044",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Or the last of my life-days live in this mead-hall.”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002045": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002045",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002046": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002046",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">These words to the lady were welcome and pleasing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002047": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002047",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002048": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002048",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The boast of the Geatman; with gold trappings broidered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002049": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002049",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002050": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002050",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Went the freeborn folk-queen her fond-lord to sit by.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002051": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002051",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Glee is high.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002052": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002052",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then again as of yore was heard in the building</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002053": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002053",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">85</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002054": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002054",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Courtly discussion, conquerors’ shouting,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002055": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002055",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002056": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002056",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Heroes were happy, till Healfdene’s son would</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002057": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002057",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002058": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002058",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Go to his slumber to seek for refreshing;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002059": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002059",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002060": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002060",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For the horrid hell-monster in the hall-building knew he</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002061": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002061",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002062": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002062",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A fight was determined,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002095\">2</$link></sup> since the light of the sun they</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002063": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002063",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">90</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002064": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002064",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">No longer could see, and lowering darkness</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002065": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002065",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002066": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002066",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er all had descended, and dark under heaven</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002067": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002067",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002068": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002068",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Shadowy shapes came shying around them.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002069": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002069",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar retires, leaving Beowulf in charge of the hall.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002070": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002070",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The liegemen all rose then. One saluted the other,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002071": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002071",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002072": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002072",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hrothgar Beowulf, in rhythmical measures,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002073": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002073",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">95</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002074": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002074",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wishing him well, and, the wassail-hall giving</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002075": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002075",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002076": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002076",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To his care and keeping, quoth he departing:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002077": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002077",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002078": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002078",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“Not to any one else have I ever entrusted,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002079": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002079",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002080": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002080",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But thee and thee only, the hall of the Danemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002081": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002081",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002082": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002082",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Since high I could heave my hand and my buckler.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002083": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002083",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">100</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002084": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002084",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Take thou in charge now the noblest of houses;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002085": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002085",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002086": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002086",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Be mindful of honor, exhibiting prowess,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002087": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002087",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002088": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002088",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Watch ’gainst the foeman! Thou shalt want no enjoyments,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002089": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002089",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002090": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002090",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Survive thou safely adventure so glorious!”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002091": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002091",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002092": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002092",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002093": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002093",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000001916\">[1]</$link> The repetition of ‘<span class=\"ang\">hwæðere</span>’ (574 and 578) is regarded by some scholars as a defect. B. suggests ‘<span class=\"ang\">swá Þær</span>’ for the first: <i>So there it befell me, etc.</i> Another suggestion is to change the second ‘<span class=\"ang\">hwæðere</span>’ into ‘<span class=\"ang\">swá Þær</span>’: <i>So there I escaped with my life, etc.</i></div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002094": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002094",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002095": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002095",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002062\">[2]</$link> Kl. suggests a period after ‘determined.’ This would give the passage as follows: <i>Since they no longer could see the light of the sun, and lowering darkness was down over all, dire under the heavens shadowy beings came going around them</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002096": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002096",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002097": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002097",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002098": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002098",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002099": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002099",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002100": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002100",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000069\">XI.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002101": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002101",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002102": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002102",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>ALL SLEEP SAVE ONE.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002103": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002103",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar retires.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002104": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002104",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then Hrothgar departed, his earl-throng attending him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002105": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002105",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002106": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002106",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Folk-lord of Scyldings, forth from the building;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002107": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002107",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002108": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002108",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The war-chieftain wished then Wealhtheow to look for,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002109": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002109",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002110": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002110",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The queen for a bedmate. To keep away Grendel</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002111": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002111",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">God has provided a watch for the hall.</span> <span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002112": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002112",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The Glory of Kings had given a hall-watch,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002113": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002113",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002114": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002114",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">As men heard recounted: for the king of the Danemen</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002115": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002115",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002116": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002116",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He did special service, gave the giant a watcher:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002117": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002117",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002118": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002118",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the prince of the Geatmen implicitly trusted</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002119": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002119",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf is self-confident</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002120": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002120",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His warlike strength and the Wielder’s protection.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002121": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002121",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He prepares for rest.</span> <span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002122": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002122",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His armor of iron off him he did then,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002123": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002123",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002124": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002124",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His helmet from his head, to his henchman committed</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002125": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002125",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002126": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002126",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His chased-handled chain-sword, choicest of weapons,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002127": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002127",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002128": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002128",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And bade him bide with his battle-equipments.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002129": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002129",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002130": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002130",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The good one then uttered words of defiance,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002131": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002131",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002132": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002132",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf Geatman, ere his bed he upmounted:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002133": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002133",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf boasts of his ability to cope with Grendel.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002134": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002134",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“I hold me no meaner in matters of prowess,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002135": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002135",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002136": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002136",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In warlike achievements, than Grendel does himself;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002137": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002137",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002138": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002138",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hence I seek not with sword-edge to sooth him to slumber,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002139": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002139",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002140": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002140",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of life to bereave him, though well I am able.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002141": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002141",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">We will fight with nature’s weapons only.</span> <span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002142": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002142",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">No battle-skill<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002203\">1</$link></sup> has he, that blows he should strike me,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002143": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002143",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002144": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002144",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To shatter my shield, though sure he is mighty</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002145": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002145",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002146": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002146",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In strife and destruction; but struggling by night we</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002147": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002147",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002148": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002148",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Shall do without edges, dare he to look for</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002149": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002149",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002150": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002150",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Weaponless warfare, and wise-mooded Father</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002151": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002151",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002152": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002152",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The glory apportion, God ever-holy,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002153": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002153",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">God may decide who shall conquer</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002154": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002154",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On which hand soever to him seemeth proper.”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002155": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002155",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002156": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002156",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then the brave-mooded hero bent to his slumber,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002157": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002157",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002158": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002158",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The pillow received the cheek of the noble;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002159": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002159",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The Geatish warriors lie down.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002160": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002160",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And many a martial mere-thane attending</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002161": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002161",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002162": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002162",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sank to his slumber. Seemed it unlikely</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002163": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002163",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">They thought it very unlikely that they should ever see their homes again.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002164": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002164",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That ever thereafter any should hope to</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002165": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002165",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002166": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002166",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Be happy at home, hero-friends visit</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002167": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002167",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002168": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002168",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Or the lordly troop-castle where he lived from his childhood;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002169": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002169",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002170": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002170",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">They had heard how slaughter had snatched from the wine-hall,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002171": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002171",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002172": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002172",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Had recently ravished, of the race of the Scyldings</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002173": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002173",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">But God raised up a deliverer.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002174": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002174",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Too many by far. But the Lord to them granted</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002175": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002175",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002176": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002176",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The weaving of war-speed, to Wederish heroes</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002177": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002177",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002178": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002178",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Aid and comfort, that every opponent</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002179": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002179",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002180": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002180",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By one man’s war-might they worsted and vanquished,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002181": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002181",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">God rules the world.</span> <span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002182": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002182",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By the might of himself; the truth is established</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002183": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002183",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002184": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002184",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That God Almighty hath governed for ages</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002185": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002185",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002186": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002186",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Kindreds and nations. A night very lurid</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002187": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002187",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Grendel comes to Heorot.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002188": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002188",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The trav’ler-at-twilight came tramping and striding.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002189": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002189",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002190": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002190",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The warriors were sleeping who should watch the horned-building,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002191": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002191",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Only one warrior is awake.</span> <span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002192": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002192",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">One only excepted. ’Mid earthmen ’twas ’stablished,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002193": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002193",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002194": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002194",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Th’ implacable foeman was powerless to hurl them</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002195": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002195",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002196": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002196",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the land of shadows, if the Lord were unwilling;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002197": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002197",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002198": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002198",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But serving as warder, in terror to foemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002199": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002199",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002200": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002200",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He angrily bided the issue of battle.<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002205\">2</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002201": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002201",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002202": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002202",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002203": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002203",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002142\">[1]</$link> Gr. understood ‘<span class=\"ang\">gódra</span>’ as meaning ‘advantages in battle.’ This rendering H.-So. rejects. The latter takes the passage as meaning that Grendel, though mighty and formidable, has no skill in the art of war.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002204": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002204",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002205": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002205",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002200\">[2]</$link> B. in his masterly articles on Beowulf (P. and B. XII.) rejects the division usually made at this point, ‘<span class=\"ang\">Þá</span>.’ (711), usually rendered ‘then,’ he translates ‘when,’ and connects its clause with the foregoing sentence. These changes he makes to reduce the number of ‘<span class=\"ang\">cóm</span>’s’ as principal verbs. (Cf. 703, 711, 721.) With all deference to this acute scholar, I must say that it seems to me that the poet is exhausting his resources to bring out clearly the supreme event on which the whole subsequent action turns. First, he (Grendel) came <i>in the wan night</i>; second, he came <i>from the moor</i>; third, he came <i>to the hall</i>. Time, place from which, place to which, are all given.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002206": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002206",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002207": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002207",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002208": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002208",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002209": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002209",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002210": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002210",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000071\">XII.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002211": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002211",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002212": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002212",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>GRENDEL AND BEOWULF.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002213": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002213",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Grendel comes from the fens.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002214": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002214",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Neath the cloudy cliffs came from the moor then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002215": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002215",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002216": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002216",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Grendel going, God’s anger bare he.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002217": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002217",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002218": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002218",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The monster intended some one of earthmen</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002219": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002219",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002220": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002220",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In the hall-building grand to entrap and make way with:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002221": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002221",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He goes towards the joyous building.</span> <span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002222": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002222",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He went under welkin where well he knew of</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002223": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002223",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002224": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002224",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The wine-joyous building, brilliant with plating,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002225": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002225",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002226": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002226",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gold-hall of earthmen. Not the earliest occasion</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002227": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002227",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">This was not his first visit there.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002228": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002228",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He the home and manor of Hrothgar had sought:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002229": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002229",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002230": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002230",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ne’er found he in life-days later nor earlier</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002231": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002231",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002232": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002232",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hardier hero, hall-thanes<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002377\">1</$link></sup> more sturdy!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002233": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002233",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002234": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002234",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then came to the building the warrior marching,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002235": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002235",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">His horrid fingers tear the door open.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002236": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002236",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bereft of his joyance. The door quickly opened</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002237": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002237",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002238": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002238",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On fire-hinges fastened, when his fingers had touched it;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002239": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002239",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002240": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002240",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The fell one had flung then—his fury so bitter—</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002241": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002241",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002242": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002242",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Open the entrance. Early thereafter</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002243": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002243",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002244": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002244",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The foeman trod the shining hall-pavement,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002245": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002245",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He strides furiously into the hall.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002246": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002246",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Strode he angrily; from the eyes of him glimmered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002247": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002247",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002248": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002248",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A lustre unlovely likest to fire.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002249": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002249",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002250": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002250",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He beheld in the hall the heroes in numbers,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002251": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002251",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002252": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002252",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A circle of kinsmen sleeping together,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002253": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002253",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He exults over his supposed prey.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002254": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002254",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A throng of thanemen: then his thoughts were exultant,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002255": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002255",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002256": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002256",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He minded to sunder from each of the thanemen</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002257": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002257",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002258": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002258",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The life from his body, horrible demon,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002259": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002259",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002260": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002260",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Ere morning came, since fate had allowed him</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002261": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002261",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Fate has decreed that he shall devour no more heroes. Beowulf suffers from suspense.</span> <span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002262": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002262",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The prospect of plenty. Providence willed not</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002263": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002263",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002264": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002264",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To permit him any more of men under heaven</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002265": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002265",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002266": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002266",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To eat in the night-time. Higelac’s kinsman</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002267": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002267",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002268": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002268",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Great sorrow endured how the dire-mooded creature</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002269": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002269",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002270": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002270",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In unlooked-for assaults were likely to bear him.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002271": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002271",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002272": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002272",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">No thought had the monster of deferring the matter,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002273": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002273",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Grendel immediately seizes a sleeping warrior, and devours him.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002274": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002274",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But on earliest occasion he quickly laid hold of</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002275": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002275",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002276": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002276",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A soldier asleep, suddenly tore him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002277": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002277",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002278": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002278",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bit his bone-prison, the blood drank in currents,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002279": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002279",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002280": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002280",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Swallowed in mouthfuls: he soon had the dead man’s</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002281": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002281",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002282": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002282",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Feet and hands, too, eaten entirely.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002283": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002283",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002284": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002284",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Nearer he strode then, the stout-hearted warrior</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002285": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002285",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf and Grendel grapple.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002286": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002286",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Snatched as he slumbered, seizing with hand-grip,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002287": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002287",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002288": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002288",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Forward the foeman foined with his hand;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002289": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002289",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002290": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002290",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Caught he quickly the cunning deviser,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002291": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002291",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002292": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002292",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On his elbow he rested. This early discovered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002293": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002293",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002294": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002294",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The master of malice, that in middle-earth’s regions,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002295": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002295",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002296": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002296",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Neath the whole of the heavens, no hand-grapple greater</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002297": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002297",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The monster is amazed at Beowulf’s strength.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002298": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002298",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In any man else had he ever encountered:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002299": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002299",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002300": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002300",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fearful in spirit, faint-mooded waxed he,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002301": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002301",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002302": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002302",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Not off could betake him; death he was pondering,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002303": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002303",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He is anxious to flee.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002304": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002304",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Would fly to his covert, seek the devils’ assembly:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002305": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002305",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002306": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002306",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His calling no more was the same he had followed</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002307": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002307",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002308": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002308",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Long in his lifetime. The liege-kinsman worthy</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002309": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002309",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf recalls his boast of the evening, and determines to fulfil it.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002310": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002310",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of Higelac minded his speech of the evening,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002311": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002311",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002312": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002312",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Stood he up straight and stoutly did seize him.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002313": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002313",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002314": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002314",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His fingers crackled; the giant was outward,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002315": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002315",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002316": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002316",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The earl stepped farther. The famous one minded</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002317": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002317",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002318": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002318",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To flee away farther, if he found an occasion,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002319": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002319",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002320": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002320",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And off and away, avoiding delay,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002321": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002321",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002322": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002322",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To fly to the fen-moors; he fully was ware of</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002323": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002323",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002324": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002324",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The strength of his grapple in the grip of the foeman.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002325": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002325",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">’Twas a luckless day for Grendel.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002326": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002326",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Twas an ill-taken journey that the injury-bringing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002327": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002327",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002328": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002328",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Harrying harmer to Heorot wandered:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002329": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002329",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The hall groans.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002330": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002330",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The palace re-echoed; to all of the Danemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002331": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002331",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002332": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002332",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Dwellers in castles, to each of the bold ones,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002333": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002333",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002334": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002334",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Earlmen, was terror. Angry they both were,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002335": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002335",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002336": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002336",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Archwarders raging.<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002379\">2</$link></sup> Rattled the building;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002337": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002337",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002338": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002338",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Twas a marvellous wonder that the wine-hall withstood then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002339": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002339",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002340": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002340",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The bold-in-battle, bent not to earthward,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002341": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002341",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">65</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002342": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002342",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Excellent earth-hall; but within and without it</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002343": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002343",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002344": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002344",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was fastened so firmly in fetters of iron,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002345": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002345",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002346": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002346",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">By the art of the armorer. Off from the sill there</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002347": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002347",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002348": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002348",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bent mead-benches many, as men have informed me,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002349": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002349",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002350": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002350",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Adorned with gold-work, where the grim ones did struggle.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002351": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002351",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">70</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002352": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002352",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The Scylding wise men weened ne’er before</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002353": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002353",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002354": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002354",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That by might and main-strength a man under heaven</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002355": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002355",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002356": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002356",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Might break it in pieces, bone-decked, resplendent,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002357": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002357",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002358": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002358",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Crush it by cunning, unless clutch of the fire</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002359": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002359",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002360": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002360",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In smoke should consume it. The sound mounted upward</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002361": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002361",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Grendel’s cries terrify the Danes.</span> <span class=\"linenum\">75</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002362": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002362",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Novel enough; on the North Danes fastened</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002363": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002363",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002364": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002364",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A terror of anguish, on all of the men there</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002365": {
            "role": "text",
