        "$:/plugins/twpub/b444daeafa3e6711/text/000002366": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002366",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who heard from the wall the weeping and plaining,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002367": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002367",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002368": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002368",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The song of defeat from the foeman of heaven,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002369": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002369",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002370": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002370",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Heard him hymns of horror howl, and his sorrow</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002371": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002371",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">80</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002372": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002372",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hell-bound bewailing. He held him too firmly</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002373": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002373",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002374": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002374",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who was strongest of main-strength of men of that era.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002375": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002375",
