        "$:/plugins/twpub/b444daeafa3e6711/text/000002387": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002387",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>GRENDEL IS VANQUISHED.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002388": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002388",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf has no idea of letting Grendel live.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002389": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002389",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For no cause whatever would the earlmen’s defender</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002390": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002390",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002391": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002391",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Leave in life-joys the loathsome newcomer,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002392": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002392",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002393": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002393",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He deemed his existence utterly useless</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002394": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002394",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002395": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002395",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To men under heaven. Many a noble</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002396": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002396",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002397": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002397",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of Beowulf brandished his battle-sword old,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002398": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002398",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002399": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002399",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Would guard the life of his lord and protector,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002400": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002400",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002401": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002401",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The far-famous chieftain, if able to do so;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002402": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002402",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002403": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002403",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">While waging the warfare, this wist they but little,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002404": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002404",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002405": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002405",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Brave battle-thanes, while his body intending</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002406": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002406",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">No weapon would harm Grendel; he bore a charmed life.</span> <span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002407": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002407",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To slit into slivers, and seeking his spirit:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002408": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002408",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002409": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002409",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That the relentless foeman nor finest of weapons</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002410": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002410",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002411": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002411",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of all on the earth, nor any of war-bills</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002412": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002412",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002413": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002413",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was willing to injure; but weapons of victory</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002414": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002414",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002415": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002415",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Swords and suchlike he had sworn to dispense with.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002416": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002416",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002417": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002417",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His death at that time must prove to be wretched,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002418": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002418",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002419": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002419",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the far-away spirit widely should journey</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002420": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002420",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002421": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002421",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Into enemies’ power. This plainly he saw then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002422": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002422",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002423": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002423",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who with mirth<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002480\">1</$link></sup> of mood malice no little</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002424": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002424",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002425": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002425",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Had wrought in the past on the race of the earthmen</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002426": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002426",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002427": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002427",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">(To God he was hostile), that his body would fail him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002428": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002428",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002429": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002429",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But Higelac’s hardy henchman and kinsman</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002430": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002430",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002431": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002431",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Held him by the hand; hateful to other</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002432": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002432",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Grendel is sorely wounded.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002433": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002433",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was each one if living. A body-wound suffered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002434": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002434",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002435": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002435",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The direful demon, damage incurable</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002436": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002436",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">His body bursts.</span> <span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002437": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002437",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was seen on his shoulder, his sinews were shivered,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002438": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002438",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002439": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002439",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His body did burst. To Beowulf was given</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002440": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002440",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002441": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002441",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Glory in battle; Grendel from thenceward</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002442": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002442",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002443": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002443",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Must flee and hide him in the fen-cliffs and marshes,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002444": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002444",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002445": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002445",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Sick unto death, his dwelling must look for</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002446": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002446",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002447": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002447",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Unwinsome and woful; he wist the more fully</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002448": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002448",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The monster flees away to hide in the moors.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002449": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002449",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The end of his earthly existence was nearing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002450": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002450",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002451": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002451",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His life-days’ limits. At last for the Danemen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002452": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002452",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002453": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002453",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When the slaughter was over, their wish was accomplished.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002454": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002454",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002455": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002455",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The comer-from-far-land had cleansed then of evil,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002456": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002456",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002457": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002457",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wise and valiant, the war-hall of Hrothgar,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002458": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002458",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002459": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002459",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Saved it from violence. He joyed in the night-work,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002460": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002460",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002461": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002461",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In repute for prowess; the prince of the Geatmen</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002462": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002462",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002463": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002463",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">For the East-Danish people his boast had accomplished,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002464": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002464",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002465": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002465",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Bettered their burdensome bale-sorrows fully,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002466": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002466",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002467": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002467",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The craft-begot evil they erstwhile had suffered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002468": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002468",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002469": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002469",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And were forced to endure from crushing oppression,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002470": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002470",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002471": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002471",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Their manifold misery. ’Twas a manifest token,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002472": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002472",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf suspends Grendel’s hand and arm in Heorot.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002473": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002473",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When the hero-in-battle the hand suspended,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002474": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002474",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002475": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002475",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The arm and the shoulder (there was all of the claw</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002476": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002476",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002477": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002477",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of Grendel together) ’neath great-stretching hall-roof.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002478": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002478",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002479": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002479",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002480": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002480",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002423\">[1]</$link> It has been proposed to translate ‘<span class=\"ang\">myrðe</span>’ by <i>with sorrow</i>; but there seems no authority for such a rendering. To the present translator, the phrase ‘<span class=\"ang\">módes myrðe</span>’ seems a mere padding for <i>gladly</i>; i.e., <i>he who gladly harassed mankind</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002481": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002481",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002482": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002482",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002483": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002483",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002484": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002484",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002485": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002485",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000075\">XIV.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002486": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002486",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002487": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002487",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>REJOICING OF THE DANES.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002488": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002488",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">At early dawn, warriors from far and near come together to hear of the night’s adventures.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002489": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002489",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In the mist of the morning many a warrior</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002490": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002490",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002491": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002491",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Stood round the gift-hall, as the story is told me:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002492": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002492",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002493": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002493",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Folk-princes fared then from far and from near</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002494": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002494",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002495": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002495",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Through long-stretching journeys to look at the wonder,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002496": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002496",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002497": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002497",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The footprints of the foeman. Few of the warriors</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002498": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002498",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Few warriors lamented Grendel’s destruction.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002499": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002499",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who gazed on the foot-tracks of the inglorious creature</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002500": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002500",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002501": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002501",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His parting from life pained very deeply,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002502": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002502",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002503": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002503",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">How, weary in spirit, off from those regions</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002504": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002504",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002505": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002505",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In combats conquered he carried his traces,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002506": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002506",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002507": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002507",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Fated and flying, to the flood of the nickers.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002508": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002508",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Grendel’s blood dyes the waters.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002509": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002509",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">There in bloody billows bubbled the currents,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002510": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002510",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002511": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002511",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The angry eddy was everywhere mingled</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002512": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002512",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002513": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002513",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And seething with gore, welling with sword-blood;<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002666\">1</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002514": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002514",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002515": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002515",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He death-doomed had hid him, when reaved of his joyance</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002516": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002516",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002517": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002517",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He laid down his life in the lair he had fled to,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002518": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002518",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002519": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002519",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His heathenish spirit, where hell did receive him.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002520": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002520",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002521": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002521",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thence the friends from of old backward turned them,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002522": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002522",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002523": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002523",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And many a younker from merry adventure,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002524": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002524",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002525": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002525",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Striding their stallions, stout from the seaward,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002526": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002526",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002527": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002527",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Heroes on horses. There were heard very often</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002528": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002528",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf is the hero of the hour.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002529": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002529",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf’s praises; many often asserted</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002530": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002530",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002531": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002531",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That neither south nor north, in the circuit of waters,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002532": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002532",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He is regarded as a probable successor to Hrothgar.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002533": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002533",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">O’er outstretching earth-plain, none other was better</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002534": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002534",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002535": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002535",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Mid bearers of war-shields, more worthy to govern,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002536": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002536",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002537": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002537",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Neath the arch of the ether. Not any, however,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002538": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002538",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002539": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002539",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Gainst the friend-lord muttered, mocking-words uttered</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002540": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002540",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">But no word is uttered to derogate from the old king</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002541": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002541",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of Hrothgar the gracious (a good king he).</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002542": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002542",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002543": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002543",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Oft the famed ones permitted their fallow-skinned horses</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002544": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002544",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002545": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002545",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To run in rivalry, racing and chasing,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002546": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002546",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002547": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002547",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Where the fieldways appeared to them fair and inviting,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002548": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002548",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002549": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002549",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Known for their excellence; oft a thane of the folk-lord,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002668\">2</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002550": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002550",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The gleeman sings the deeds of heroes.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002551": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002551",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\"><sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002670\">3</$link></sup>A man of celebrity, mindful of rhythms,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002552": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002552",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002553": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002553",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Who ancient traditions treasured in memory,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002554": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002554",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002555": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002555",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">New word-groups found properly bound:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002556": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002556",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002557": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002557",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The bard after ’gan then Beowulf’s venture</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002558": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002558",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He sings in alliterative measures of Beowulf’s prowess.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002559": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002559",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wisely to tell of, and words that were clever</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002560": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002560",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002561": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002561",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To utter skilfully, earnestly speaking,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002562": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002562",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002563": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002563",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Everything told he that he heard as to Sigmund’s</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002564": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002564",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Also of Sigemund, who has slain a great fire-dragon.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002565": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002565",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Mighty achievements, many things hidden,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002566": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002566",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002567": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002567",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The strife of the Wælsing, the wide-going ventures</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002568": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002568",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002569": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002569",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The children of men knew of but little,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002570": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002570",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002571": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002571",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The feud and the fury, but Fitela with him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002572": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002572",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002573": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002573",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">When suchlike matters he minded to speak of,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002574": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002574",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002575": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002575",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Uncle to nephew, as in every contention</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002576": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002576",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002577": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002577",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Each to other was ever devoted:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002578": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002578",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002579": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002579",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A numerous host of the race of the scathers</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002580": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002580",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002581": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002581",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">They had slain with the sword-edge. To Sigmund accrued then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002582": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002582",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002583": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002583",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">No little of glory, when his life-days were over,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002584": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002584",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002585": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002585",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Since he sturdy in struggle had destroyed the great dragon,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002586": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002586",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002587": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002587",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The hoard-treasure’s keeper; ’neath the hoar-grayish stone he,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002588": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002588",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002589": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002589",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The son of the atheling, unaided adventured</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002590": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002590",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002591": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002591",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The perilous project; not present was Fitela,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002592": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002592",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002593": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002593",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Yet the fortune befell him of forcing his weapon</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002594": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002594",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002595": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002595",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Through the marvellous dragon, that it stood in the wall,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002596": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002596",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002597": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002597",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Well-honored weapon; the worm was <span class=\"corr\">slaughtered</span>.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002598": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002598",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002599": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002599",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The great one had gained then by his glorious achievement</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002600": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002600",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002601": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002601",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To reap from the ring-hoard richest enjoyment,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002602": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002602",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002603": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002603",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">As best it did please him: his vessel he loaded,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002604": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002604",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002605": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002605",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Shining ornaments on the ship’s bosom carried,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002606": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002606",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">60</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002607": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002607",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Kinsman of Wæls: the drake in heat melted.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002608": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002608",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Sigemund was widely famed.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002609": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002609",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He was farthest famed of fugitive pilgrims,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002610": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002610",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002611": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002611",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Mid wide-scattered world-folk, for works of great prowess,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002612": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002612",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002613": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002613",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">War-troopers’ shelter: hence waxed he in honor.<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002672\">4</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002614": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002614",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Heremod, an unfortunate Danish king, is introduced by way of contrast.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002615": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002615",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Afterward Heremod’s hero-strength failed him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002616": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002616",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">65</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002617": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002617",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His vigor and valor. ’Mid venomous haters</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002618": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002618",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002619": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002619",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the hands of foemen he was foully delivered,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002620": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002620",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002621": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002621",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Offdriven early. Agony-billows</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002622": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002622",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Unlike Sigemund and Beowulf, Heremod was a burden to his people.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002623": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002623",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Oppressed him too long, to his people he became then,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002624": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002624",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002625": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002625",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To all the athelings, an ever-great burden;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002626": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002626",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">70</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002627": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002627",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">And the daring one’s journey in days of yore</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002628": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002628",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002629": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002629",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Many wise men were wont to deplore,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002630": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002630",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002631": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002631",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Such as hoped he would bring them help in their sorrow,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002632": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002632",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002633": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002633",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That the son of their ruler should rise into power,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002634": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002634",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002635": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002635",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Holding the headship held by his fathers,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002636": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002636",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">75</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002637": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002637",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Should govern the people, the gold-hoard and borough,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002638": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002638",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002639": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002639",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The kingdom of heroes, the realm of the Scyldings.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002640": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002640",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf is an honor to his race.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002641": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002641",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He to all men became then far more beloved,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002642": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002642",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002643": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002643",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Higelac’s kinsman, to kindreds and races,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002644": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002644",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002645": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002645",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To his friends much dearer; him malice assaulted.—</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002646": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002646",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">The story is resumed.</span> <span class=\"linenum\">80</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002647": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002647",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Oft running and racing on roadsters they measured</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002648": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002648",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002649": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002649",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The dun-colored highways. Then the light of the morning</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002650": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002650",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002651": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002651",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Was hurried and hastened. Went henchmen in numbers</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002652": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002652",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002653": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002653",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To the beautiful building, bold ones in spirit,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002654": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002654",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002655": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002655",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To look at the wonder; the liegelord himself then</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002656": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002656",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">85</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002657": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002657",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From his wife-bower wending, warden of treasures,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002658": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002658",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002659": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002659",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Glorious trod with troopers unnumbered,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002660": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002660",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002661": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002661",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Famed for his virtues, and with him the queen-wife</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002662": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002662",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002663": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002663",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Measured the mead-ways, with maidens attending.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002664": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002664",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002665": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002665",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002666": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002666",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002513\">[1]</$link> S. emends, suggesting ‘<span class=\"ang\">déop</span>’ for ‘<span class=\"ang\">déog</span>,’ and removing semicolon after ‘<span class=\"ang\">wéol</span><span class=\"add\">.</span>’ The two half-lines ‘welling … hid him’ would then read: <i>The bloody deep welled with sword-gore</i>. B. accepts ‘<span class=\"ang\">déop</span>’ for ‘<span class=\"ang\">déog</span>,’ but reads ‘<span class=\"ang\">déað-fæges</span>’: <i>The deep boiled with the sword-gore of the death-doomed one</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002667": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002667",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002668": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002668",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002549\">[2]</$link> Another and quite different rendering of this passage is as follows: <i>Oft a liegeman of the king, a fame-covered man mindful of songs, who very many ancient traditions remembered (he found other word-groups accurately bound together) began afterward to tell of Beowulf’s adventure, skilfully to narrate it, etc</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002669": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002669",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002670": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002670",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002551\">[3]</$link> Might ‘<span class=\"ang\">guma gilp-hladen</span>’ mean ‘a man laden with boasts of the deeds of others’?</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002671": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002671",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002672": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002672",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\"><div class=\"footnote\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002613\">[4]</$link> t.B. accepts B.’s ‘<span class=\"ang\">hé þæs áron þáh</span>’ as given by H.-So., but puts a comma after ‘<span class=\"ang\">þáh</span>,’ and takes ‘<span class=\"ang\">siððan</span>’ as introducing a dependent clause: <i>He throve in honor since Heremod’s strength … had decreased</i>.</div></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002673": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002673",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"footnotes\">\n</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002674": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002674",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002675": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002675",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\">\n</div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002676": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002676",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002677": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002677",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2 class=\"num\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000000077\">XV.</$link></h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002678": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002678",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002679": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002679",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><h2>HROTHGAR’S GRATITUDE.</h2></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002680": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002680",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002681": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002681",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hrothgar discoursed (to the hall-building went he,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002682": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002682",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002683": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002683",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He stood by the pillar,<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002812\">1</$link></sup> saw the steep-rising hall-roof</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002684": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002684",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002685": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002685",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gleaming with gold-gems, and Grendel his hand there):</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002686": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002686",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hrothgar gives thanks for the overthrow of the monster.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002687": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002687",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“For the sight we behold now, thanks to the Wielder</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002688": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002688",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">5</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002689": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002689",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Early be offered! Much evil I bided,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002690": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002690",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002691": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002691",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Snaring from Grendel:<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002814\">2</$link></sup> God can e’er ’complish</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002692": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002692",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002693": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002693",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Wonder on wonder, Wielder of Glory!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002694": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002694",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I had given up all hope, when this brave liegeman came to our aid.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002695": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002695",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But lately I reckoned ne’er under heaven</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002696": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002696",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002697": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002697",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Comfort to gain me for any of sorrows,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002698": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002698",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">10</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002699": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002699",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">While the handsomest of houses horrid with bloodstain</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002700": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002700",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002701": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002701",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gory uptowered; grief had offfrightened<sup class=\"footnoteref\"><$link class=\"pginternal\" to=\"$:/plugins/twpub/b444daeafa3e6711/text/000002816\">3</$link></sup></div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002702": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002702",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002703": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002703",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Each of the wise ones who weened not that ever</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002704": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002704",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002705": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002705",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The folk-troop’s defences ’gainst foes they should strengthen,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002706": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002706",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002707": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002707",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Gainst sprites and monsters. Through the might of the Wielder</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002708": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002708",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">15</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002709": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002709",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">A doughty retainer hath a deed now accomplished</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002710": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002710",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002711": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002711",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Which erstwhile we all with our excellent wisdom</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002712": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002712",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">If his mother yet liveth, well may she thank God for this son.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002713": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002713",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Failed to perform. May affirm very truly</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002714": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002714",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002715": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002715",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">What woman soever in all of the nations</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002716": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002716",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002717": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002717",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Gave birth to the child, if yet she surviveth,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002718": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002718",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">20</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002719": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002719",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">That the long-ruling Lord was lavish to herward</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002720": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002720",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002721": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002721",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In the birth of the bairn. Now, Beowulf dear,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002722": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002722",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Hereafter, Beowulf, thou shalt be my son.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002723": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002723",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Most excellent hero, I’ll love thee in spirit</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002724": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002724",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002725": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002725",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">As bairn of my body; bear well henceforward</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002726": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002726",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002727": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002727",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The relationship new. No lack shall befall thee</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002728": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002728",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">25</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002729": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002729",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Of earth-joys any I ever can give thee.</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002730": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002730",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002731": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002731",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Full often for lesser service I’ve given</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002732": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002732",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<a class=\"x-ebookmaker-pageno\"></a>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002733": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002733",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Hero less hardy hoard-treasure precious,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002734": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002734",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Thou hast won immortal distinction.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002735": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002735",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">To a weaker in war-strife. By works of distinction</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002736": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002736",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002737": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002737",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thou hast gained for thyself now that thy glory shall flourish</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002738": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002738",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">30</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002739": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002739",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Forever and ever. The All-Ruler quite thee</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002740": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002740",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002741": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002741",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With good from His hand as He hitherto did thee!”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002742": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002742",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Beowulf replies: I was most happy to render thee this service.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002743": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002743",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Beowulf answered, Ecgtheow’s offspring:</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002744": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002744",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002745": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002745",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">“That labor of glory most gladly achieved we,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002746": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002746",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002747": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002747",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The combat accomplished, unquailing we ventured</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002748": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002748",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">35</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002749": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002749",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The enemy’s grapple; I would grant it much rather</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002750": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002750",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002751": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002751",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Thou wert able to look at the creature in person,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002752": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002752",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002753": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002753",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Faint unto falling, the foe in his trappings!</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002754": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002754",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002755": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002755",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">On murder-bed quickly I minded to bind him,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002756": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002756",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002757": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002757",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">With firm-holding fetters, that forced by my grapple</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002758": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002758",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">40</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002759": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002759",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Low he should lie in life-and-death struggle</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002760": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002760",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002761": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002761",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Less his body escape; I was wholly unable,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002762": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002762",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">I could not keep the monster from escaping, as God did not will that I should.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002763": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002763",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Since God did not will it, to keep him from going,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002764": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002764",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002765": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002765",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Not held him that firmly, hated opposer;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002766": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002766",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002767": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002767",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Too swift was the foeman. Yet safety regarding</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002768": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002768",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">45</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002769": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002769",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">He suffered his hand behind him to linger,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002770": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002770",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002771": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002771",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">His arm and shoulder, to act as watcher;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002772": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002772",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">He left his hand and arm behind.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002773": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002773",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">No shadow of solace the woe-begone creature</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002774": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002774",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002775": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002775",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Found him there nathless: the hated destroyer</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002776": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002776",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002777": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002777",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Liveth no longer, lashed for his evils,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002778": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002778",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">50</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002779": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002779",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">But sorrow hath seized him, in snare-meshes hath him</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002780": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002780",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002781": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002781",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Close in its clutches, keepeth him writhing</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002782": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002782",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002783": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002783",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">In baleful bonds: there banished for evil</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002784": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002784",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002785": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002785",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">The man shall wait for the mighty tribunal,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002786": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002786",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">God will give him his deserts.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002787": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002787",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">How the God of glory shall give him his earnings.”</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002788": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002788",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"linenum\">55</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002789": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002789",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Then the soldier kept silent, son of old Ecglaf,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002790": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002790",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n<span class=\"sidenote\">Unferth has nothing more to say, for Beowulf’s actions speak louder than words.</span>\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002791": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002791",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">From boasting and bragging of battle-achievements,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002792": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002792",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002793": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002793",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Since the princes beheld there the hand that depended</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002794": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002794",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002795": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002795",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">’Neath the lofty hall-timbers by the might of the nobleman,</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002796": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002796",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\">\n</div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002797": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002797",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-b444daeafa3e6711-0bfa64 twpub-b444daeafa3e6711-e8bee0 twpub-b444daeafa3e6711-6b4cd0\"><div class=\"div1 c12\"><div class=\"fit\"><div class=\"l\">Each one before him, the enemy’s fingers;</div></div></div></div>"
        },
        "$:/plugins/twpub/b444daeafa3e6711/text/000002798": {
            "role": "text",
            "title": "$:/plugins/twpub/b444daeafa3e6711/text/000002798",
            "type": "text/vnd.twpub",
